mapper.select:
select

from dota2_match_details 


	           and match_id=#{matchId}


	           and radiant_win=#{radiantWin}


	           and duration=#{duration}


	           and season=#{season}


	           and start_time=#{startTime}


	           and match_seq_num=#{matchSeqNum}


	           and tower_status_radiant=#{towerStatusRadiant}


	           and tower_status_dire=#{towerStatusDire}


	           and barracks_status_radiant=#{barracksStatusRadiant}


	           and barracks_status_dire=#{barracksStatusDire}


	           and cluster=#{cluster}


	           and first_blood_time=#{firstBloodTime}


	           and lobby_type=#{lobbyType}


	           and human_players=#{humanPlayers}


	           and leagueid=#{leagueid}


	           and positive_votes=#{positiveVotes}


	           and negative_votes=#{negativeVotes}


	           and game_mode=#{gameMode}


	           and flags=#{flags}


	           and engine=#{engine}


	           and radiant_score=#{radiantScore}


	           and dire_score=#{direScore}


	           and radiant_team_id=#{radiantTeamId}


	           and radiant_name=#{radiantName}


	           and radiant_logo=#{radiantLogo}


	           and radiant_team_complete=#{radiantTeamComplete}


	           and dire_team_id=#{direTeamId}


	           and dire_name=#{direName}


	           and dire_logo=#{direLogo}


	           and dire_team_complete=#{direTeamComplete}


	           and radiant_captain=#{radiantCaptain}


	           and dire_captain=#{direCaptain}


	           and players=#{players}


	           and picks_bans=#{picksBans}

mapper.insert:
INSERT INTO dota2_match_details (
	match_id, radiant_win, duration, season, start_time, match_seq_num, tower_status_radiant, tower_status_dire, barracks_status_radiant, barracks_status_dire, cluster, first_blood_time, lobby_type, human_players, leagueid, positive_votes, negative_votes, game_mode, flags, engine, radiant_score, dire_score, radiant_team_id, radiant_name, radiant_logo, radiant_team_complete, dire_team_id, dire_name, dire_logo, dire_team_complete, radiant_captain, dire_captain, players, picks_bans
) VALUES (
	#{matchId}, #{radiantWin}, #{duration}, #{season}, #{startTime}, #{matchSeqNum}, #{towerStatusRadiant}, #{towerStatusDire}, #{barracksStatusRadiant}, #{barracksStatusDire}, #{cluster}, #{firstBloodTime}, #{lobbyType}, #{humanPlayers}, #{leagueid}, #{positiveVotes}, #{negativeVotes}, #{gameMode}, #{flags}, #{engine}, #{radiantScore}, #{direScore}, #{radiantTeamId}, #{radiantName}, #{radiantLogo}, #{radiantTeamComplete}, #{direTeamId}, #{direName}, #{direLogo}, #{direTeamComplete}, #{radiantCaptain}, #{direCaptain}, #{players}, #{picksBans}
)

